	public function publish_header_image() {
		if ( $this->has_header_image ) {
			$this->header_image->publish();
		}
	}

	public function publish_sections() {
		if ( count( $this->sections ) ) {
			// Loop through sections.
			foreach ( $this->sections as $section ) {
				$section->publish();
			}
		}
	}


